/*global tinymce:true */

tinymce.PluginManager.add('bcpblocks', function(editor) {
    
    function add2Cols(){
        
        var tmpDate = new Date(),
            picIdKey = tmpDate.getTime();
        
        editor.insertContent(
            '<div class="text-content-row" >'
                +'<div class="text-content-col size-half align-inline" >Left</div>'
                +'<div class="text-content-col size-half align-inline" >Right</div>'
                +'</div><p>&nbsp;</p>'
        );
     
    };
    
    function add3Cols(){
        
        var tmpDate = new Date(),
            picIdKey = tmpDate.getTime();
        
        editor.insertContent(
            '<div class="text-content-row" >'
                +'<div class="text-content-col size-third align-inline" >Left</div>'
                +'<div class="text-content-col size-third align-inline" >Middle</div>'
                +'<div class="text-content-col size-third align-inline" >Right</div>'
            +'</div><p>&nbsp;</p>'
        );
     
    };

    editor.addButton('bcpblocks2', {
        text: '2 C',
        tooltip: 'Insert/edit image',
        onclick: add2Cols//,
        //stateSelector: 'img:not([data-mce-object],[data-mce-placeholder])'
    });
    
    editor.addButton('bcpblocks3', {
        text: '3 C',
        tooltip: 'Insert/edit image',
        onclick: add3Cols//,
        //stateSelector: 'img:not([data-mce-object],[data-mce-placeholder])'
    });

    /*
    editor.addMenuItem('bcpblocks2', {
        text: '2 C',
        text: 'Insert/edit image',
        onclick: placeImage,
        context: 'insert',
        prependToContext: true
    });
    */

});
